/**
 * Movipress versions.php
 *
 * Initializes some data for the plugin
 *
 * @author 		Movipress Ltd.
 * @version     1.0
 */
	

// Initialize data
function mpio_init_option($option_name, $value){
	$option = get_option($option_name);
	if ($option === false){
		add_option($option_name, $value);
	}
}
	
// Initialize the settings versions to sync with the app.
function mpio_init_versions(){
	$default_versions = array(
		'general'		=> '1',	
		'app_menu'		=> '1',
		'permalinks'	=> '1',
		'categories'	=> '1',
		'tags'			=> '1',
		'users'			=> '1',
		'templates'		=> '1',
		'ads'			=> '1'
	);
	add_option('mpio_versions', $default_versions);
}
 
// Initializes notifications options
function mpio_init_notifications_options(){
	$def_notifications = array(
						'fcm_api_key' => '',
						'send_notification_new_post' => '1',
					);
	mpio_init_option('mpio_notifications_options',$def_notifications);
}

// Initializes app links options 
function mpio_init_applinks_options(){
	$def_applinks = array(
						'app_package_name' => 'com.example.app',
						'in_home' => '1',
						'in_posts' => '1',
						'in_pages' => '1',
						'in_categories' => '1',
						'in_tags' => '1',
						'in_authors' => '1',
					);
	mpio_init_option('mpio_app_links_options',$def_applinks);
}

					
